mapper.select:
select refund."refundRequestTime", "deliveryProduct"."productCount", product.brand, product."pName", product.price, "productMainImage"."imagePath", delivery."customerId", delivery."deliveryId", ROW_NUMBER()OVER(ORDER BY delivery."deliveryId" DESC) AS ROW_NUM, COUNT(*) OVER(PARTITION BY delivery."deliveryId") AS PARTCNT from delivery join "deliveryProduct" on delivery."deliveryId" = "deliveryProduct"."deliveryId" join product on "deliveryProduct"."productId" =product."productId" join "productMainImage" on product."productId" = "productMainImage"."productId" join refund on delivery."deliveryId" = refund."deliveryId" where delivery."customerId" = cast(#{customerId} as UUID) AND (refund."refundRequestTime" >= to_timestamp(#{startDate}, 'YYYY-MM-DD 00:00:00.00') AND refund."refundRequestTime" <= to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS.MS'))
AND (product.brand LIKE #{searchWord} OR product."pName" LIKE #{searchWord})
order by refund."refundRequestTime" DESC;
