mapper.select:
select delivery."purchaseDateTime", delivery."deliveryId", delivery."customerId", delivery."deliveryStatus", "deliveryProduct"."productCount", product."productId", product.brand, product."pName", product.price, "productMainImage"."imagePath", ROW_NUMBER()OVER(ORDER BY delivery."deliveryId" DESC) AS ROW_NUM, COUNT(*) OVER(PARTITION BY delivery."deliveryId") AS PARTCNT from delivery join "deliveryProduct" on delivery."deliveryId" = "deliveryProduct"."deliveryId" join product on "deliveryProduct"."productId" = product."productId" left outer join "productMainImage" on "deliveryProduct"."productId" = "productMainImage"."productId" join customer on delivery."customerId" = customer."customerId" where delivery."customerId" = cast(#{customerId} as UUID) AND (delivery."purchaseDateTime" >= to_timestamp(#{startDate}, 'YYYY-MM-DD 00:00:00.00') AND delivery."purchaseDateTime" <= to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS.MS'))
AND (product.brand LIKE #{searchWord} OR product."pName" LIKE #{searchWord})
order by delivery."purchaseDateTime" DESC LIMIT 5 OFFSET #{page}

select refund."refundRequestTime", "deliveryProduct"."productCount", product."productId", product.brand, product."pName", product.price, "productMainImage"."imagePath", delivery."customerId", delivery."deliveryId", ROW_NUMBER()OVER(ORDER BY delivery."deliveryId" DESC) AS ROW_NUM, COUNT(*) OVER(PARTITION BY delivery."deliveryId") AS PARTCNT from delivery join "deliveryProduct" on delivery."deliveryId" = "deliveryProduct"."deliveryId" join product on "deliveryProduct"."productId" =product."productId" left outer join "productMainImage" on product."productId" = "productMainImage"."productId" join refund on delivery."deliveryId" = refund."deliveryId" where delivery."customerId" = cast(#{customerId} as UUID) AND (refund."refundRequestTime" >= to_timestamp(#{startDate}, 'YYYY-MM-DD 00:00:00.00') AND refund."refundRequestTime" <= to_timestamp(#{endDate}, 'YYYY-MM-DD HH24:MI:SS.MS'))
AND (product.brand LIKE #{searchWord} OR product."pName" LIKE #{searchWord})
order by refund."refundRequestTime" DESC LIMIT 5 OFFSET #{page}

select "productMainImage"."imagePath", product."productId", product.brand, product."pName", product.price from customer join "likedProduct" on customer."customerId" = "likedProduct"."customerId" join product on "likedProduct"."productId" = "product"."productId" left outer join "productMainImage" on "productMainImage"."productId" = "product"."productId" where customer."customerId" = cast(#{customerId} as UUID)
AND (product.brand LIKE #{searchWord} OR product."pName" LIKE #{searchWord})
LIMIT 8 OFFSET #{page}
